CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'Js@81051';

CREATE DATABASE SCOPED CREDENTIAL cred_ab
WITH IDENTITY = 'Managed Identity';

CREATE EXTERNAL DATA SOURCE silver_source
WITH
(
    LOCATION = 'https://abprojectlake.blob.core.windows.net/silver',
    CREDENTIAL = cred_ab

);

CREATE EXTERNAL DATA SOURCE source_gold
WITH
(
    LOCATION = 'https://abprojectlake.blob.core.windows.net/gold',
    CREDENTIAL = cred_ab
);

CREATE EXTERNAL FILE FORMAT parquet_format
WITH
(
    FORMAT_TYPE = PARQUET,
    DATA_COMPRESSION = 'org.apache.hadoop.io.compress.SnappyCodec'
);

------------------------------------------------------------------------

CREATE EXTERNAL TABLE gold.ext_sales
WITH
(
    LOCATION = 'ext_sales',
    DATA_SOURCE = source_gold,
    FILE_FORMAT = parquet_format
)
AS
SELECT * FROM gold.sales_V;

SELECT * FROM gold.ext_sales;
